    @Test
    fun whatIsTheDateToPrintRealDate() {

        var updated_date = "2019-06-18T06:47:20-04:00"

        val test = parseDatas().whatIsTheDateToPrint(updated_date)

        assertEquals("18/06/2019", test)
    }

    @Test
    fun whatIsTheDateToPrintNotARealDate() {

        var updated_date = "2019-06-17T06:47:20-04:00"

        val test = parseDatas().whatIsTheDateToPrint(updated_date)

        assertNotEquals("18/06/2019", test)
    }

